"""
This python script was created by Cybertron, Nurarihyon, and SinsOfSeven
It aims to make shapekey like behavior using position information, currently the process is quite trial and error.

Run this script inside the folder containing the PositionBuffers and Mod ini you wish to apply the slider generator to.

"""

import os
import base64
import argparse
import shutil

PROGRAM_NAME = 'SliderImpactGenerator'
VERSION = '1.3.5'

# MARK: MAIN
def main():
    parser = argparse.ArgumentParser(
        description= f"============ ShapeKey Slider Script Help ============", 
            epilog=   "====================================================="
    )
    parser.add_argument('-v',"--version",default=VERSION, action="version", version="%(name)s %(ver)s" % {'name':PROGRAM_NAME,'ver': VERSION})
    parser.add_argument("-i", "--input", metavar='DIR', type=str, default = os.getcwd(), help="Input directory")
    parser.add_argument("-o", "--output", metavar='DIR', type=str, default = os.getcwd(), help="Output directory")

    parser.add_argument("--order", action="store_true", help="Rearrage slider order (Prompt).")
    parser.add_argument("--size", metavar='n', default=[0.15, 0.5, 0.8, 0.05], action="store", nargs=4, type=float, help="UI (0,1): x y size, x y position")
    parser.add_argument("-n", "--names", default = [], action="store", nargs='*', help="Names of the sliders")
    parser.add_argument("--scale", default=1, type=float, help="Slider Multiplier, default is 1")
    parser.add_argument("-cm", "--metric", action="store_true", help="Adds incremental measurements to the bars.")

    parser.add_argument("-ns", "--noside", action="store_true", help="Disables sidebar")
    parser.add_argument("-nb", "--noborder", action="store_true", help="Disables border")
    parser.add_argument("--author", action="store_true", help="Generate placeholder text images for author")
    parser.add_argument("--text", action="store_true", help="Generate placeholder text images for sliders.")
    parser.add_argument("--title", action="store_true", help="Generate placeholder text images for title")

    args = parser.parse_args()
    text=args.text
    title = args.title
    author = args.author
    noside = args.noside

    # Eat me, I don't like writting to a new folder as default behavior.
    if args.input != os.getcwd():
        args.input = os.path.join(os.getcwd(),args.input)
    if args.output != os.getcwd():
        args.output = os.path.join(os.getcwd(),args.output)

    resourcedir = os.path.join(args.output, 'resources')
    inis = [os.path.join(root, filename) for root, dirs, files in os.walk(args.input) for filename in files 
        if filename.lower().endswith('.ini') and not filename.lower().startswith('disabled')]
    if len(inis) == 0:
        raise ValueError("ERROR: Could not find any .ini files in the directory")
    mainini = inis[0]
    if len(inis) >= 2:
        mainini = inis[PromptMainIni()]
    charaname = os.path.basename(mainini)[:-4]
    print('\nCharacter: '+charaname)


    # Cope patch for single folder exectution.
    if args.input == args.output and args.names == []:
        [args.names.append(filename[len(f'{charaname}Position'):-4]) for root, dirs, files in os.walk(args.input) for filename in files
                    if filename.lower().startswith(f'{charaname}Position'.lower())]

    posbuffers = [os.path.join(root, filename) for root, dirs, files in os.walk(args.input) for filename in files
                if filename.lower().startswith(f'{charaname}Position'.lower())]
    baseposition = posbuffers.pop(0) if len(posbuffers) > 0 else None
 
    if args.order and len(posbuffers) >= 2:
        posbuffers = PromptUserOrder(posbuffers)

    numberoffiles = len(posbuffers)

    if args.names == []:
        args.names = [f"Slider{i+1}" for i in range(len(posbuffers))]
    
    if len(args.names) < len(posbuffers):
        raise ValueError("ERROR: Please enter the same number of names as the number of mods")
    elif len(args.names) > len(posbuffers):
        print("WARNING: More names than the number of mods, the extra names will be ignored")
        args.names = args.names[:len(posbuffers)]

    print(numberoffiles, charaname, posbuffers, baseposition)

    outputini = os.path.join(args.output, f'{charaname}.ini')
    if not os.path.exists(args.output):
        os.makedirs(args.output)
    if not os.path.exists(resourcedir):
        os.makedirs(resourcedir)
    
    CopyBasefiles(args.output, os.path.dirname(inis[0]))
    WriteINIFile(mainini, outputini, charaname, args)
    MoveAndRenamePositions(args.output, baseposition, posbuffers, args.names, charaname)
    GenerateShaderFiles(resourcedir)
    GenerateTextureFiles(resourcedir)
    for i in range(numberoffiles):
        if not noside:
            GenerateIconFiles(i+1,resourcedir)
        if text:
            GenerateTextFiles(i+1,resourcedir)
    if title:
        GenerateTextFiles('Title',resourcedir)
    if author:
        GenerateTextFiles('Author',resourcedir)

# MARK: USER ORDER
def PromptUserOrder(posbuffers):
    # Get User defined order of sliders.
    print("---Select Slider Order---")
    print("Select the order of the sliders by listing them like so '1 0 2'")
    print("Omitting an option will prevent it from generating.")
    print("Press Enter to keep default order.")
    for i, posbuffer in enumerate(posbuffers):
            print(f"\t{i}:  {posbuffer}")
    print("\nEnter selection:")

    choice = input()

    # User entered data before pressing enter
    while choice:
        choice = choice.strip().split(" ")

        if len(choice) > len(posbuffers):
            print("\nERROR: too many selections\n")
            choice = input()
        else:
            try:
                result = []
                choice = [int(x) for x in choice]
                if len(set(choice)) != len(choice):
                    print("\nERROR: do not repeat selections\n")
                    choice = input()
                elif max(choice) >= len(posbuffers):
                    print("\nERROR: selection is invalid\n")
                    choice = input()
                elif min(choice) < 0:
                    print("\nERROR: selection is invalid\n")
                    choice = input()
                    print()
                else:
                    for x in choice:
                        result.append(posbuffers[x])
                    return result
            except ValueError:
                print("\nERROR: select order by index separated by spaces (example: 3 0 1 2)\n")
                choice = input()

    return posbuffers

def PromptMainIni(inis):
    print("---Select Main INI---")
    print("Press Enter to keep default.")
    for i, inis in enumerate(inis):
            print(f"\t{i}:  {inis}")
    print("\nEnter selection:")
    choice = input()
    while choice:
        choice = choice.strip().split(" ")
        try:
            choice = [int(x) for x in choice]
            if max(choice) > 1:
                print("\nERROR: selection is invalid\n")
                choice = input()
            elif min(choice) < 0:
                print("\nERROR: selection is invalid\n")
                choice = input()
                print()
            else:
                return int(choice[0])
        except TypeError:
            choice = input()

# MARK: BUILD FROM FOLDERS
def CopyBasefiles(output, origindir):
    try:
        for root, dirs, files in os.walk(origindir):
            for filename in files:
                if 'position.buf' not in filename.lower():
                    shutil.copy(os.path.join(root, filename), output)
    except shutil.SameFileError:
        print("File Exists, Skipping")


def MoveAndRenamePositions(output, baseposition, posbuffers, names, charaname):
    try:
        for i, posbuffer in enumerate(posbuffers):
            shutil.copy(posbuffer, os.path.join(output, f'{charaname}Position{names[i]}.buf'))
        shutil.copy(baseposition, os.path.join(output, f'{charaname}PositionBase.buf'))
    except shutil.SameFileError:
        print("File Exists, Skipping")

